```javascript
const { SlashCommandBuilder } = require('discord.js');
const fs = require('fs');

const commandData = new SlashCommandBuilder()
  .setName('ban')
  .setDescription('Bans a user from the server')
  .addUserOption((option) =>
    option.setName('user').setDescription('The user to ban')
  )
  .addIntegerOption((option) =>
    option.setName('days').setDescription('The number of days the user should be banned for')
  )
  .addIntegerOption((option) =>
    option.setName('hours').setDescription('The number of hours the user should be banned for')
  );

const banCommand = async (interaction) => {
  const user = interaction.options.getUser('user');
  const days = interaction.options.getInteger('days');
  const hours = interaction.options.getInteger('hours');

  if (!user) {
    return interaction.reply({
      content: 'Please provide a user to ban!',
      ephemeral: true,
    });
