      return interaction.reply({
        content: `**${user.username}** is currently banned until ${banDate.toLocaleDateString()} and will be unbanned ${remainingDaysString}.`, // eslint-disable-line no-useless-escape, max-len
        ephemeral: true,
      });
    }
  } else if (hours) { // If only hours are specified, unban the user after the specified number of hours.
    const remainingHours = hours; // The remaining number of hours until the user is unbanned.
    const currentDate = new Date(); // Get the current date and time for formatting purposes later.

    return interaction.reply({ // Reply with a message indicating how long the user is banned for and when they'll be unbanned.
      content: `**${user.username}** is currently banned for ${hours} hour(s) and will be unbanned at ${currentDate.toLocaleTimeString()} on ${currentDate.toLocaleDateString()}`, // eslint-disable-line no-useless-escape, max-len, no-inner-declarations, object-curly-newline, consistent-return, indent, line-breaks, padded-blocks, space-before-consecutive-assignment, space-in-parens, space-unary-ops, trailing-comma, whitespace, yoda 
      ephemeral: true, 
