  } else { // If neither days nor hours are specified, ban indefinitely or until the bot is removed/disabled.
    return interaction.reply({ // Reply with a message indicating that the user is banned indefinitely or until the bot is removed/disabled.
      content: `**${user.username}** has been permanently banned from this server or until the bot is removed/disabled.`, // eslint-disable-line no-useless-escape, max-len, no-inner-declarations, object-curly-newline, consistent-return, indent, line-breaks, padded-blocks, space-before-consecutive-assignment, space-in-parens, space-unary-ops, trailing-comma, whitespace, yoda 
      ephemeral: true,  
    });    
  }    
};  

const commands = [commandData];  
