def longest_common_subsequence(str1, str2):
    def lcs_helper(i, j, common):
        if i == 0 or j == 0:
            return len(common)
        elif str1[i] == str2[j]:
            return lcs_helper(i - 1, j - 1, common + [str1[i]])
        else:
