    # Initialize a list to store the Fibonacci numbers
    fib_list = []

    # Recursive function to calculate the nth Fibonacci number
    def fib(k):
        if k == 0:
            fib_list.append(0)
        elif k == 1:
            fib_list.append(1)
        else:
            fib_list.append(fib(k-1) + fib(k-2))

