#include <string>
#include <sstream>
#include <vector>
#include <regex>

std::string markdownToHtml(std::string markdown) {
    std::string html;

    // Replace all { with <span class="">
    markdown = std::regex::replace(markdown, std::regex("\\\\{"), "<span class=\"\">");

    // Replace all } with </span>
    markdown = std::regex::replace(markdown, std::regex("\\\\}"), "</span>");

    // Replace all * with <em>
    markdown = std::regex::replace(markdown, std::regex("\\\\*"), "<em>");

    // Replace all _ with <u>
    markdown = std::regex::replace(markdown, std::regex("\\\\_"), "<u>");

    // Replace all ** with <strong>
    markdown = std::regex::replace(markdown, std::regex("\\\\\\*"), "<strong>");

    // Replace all # with <a href="">
    markdown = std::regex::replace(markdown, std::regex("\\\\#"), "<a href=\"\">");

    // Replace all ! with <img src="">
    markdown = std::regex::replace(markdown, std::regex("\\\\!"), "<img src=\"\" alt=\"\">");

    // Replace all ~ with <span class="">
    markdown = std::regex::replace(markdown, std::regex("\\\\~"), "<span class=\"\">");

    // Replace all [] with <span class="">
    markdown = std::regex::replace(markdown, std::regex("\\\\["), "<span class=\"\">");

    // Replace all () with </span>
    markdown = std::regex::replace(markdown, std::regex("\\\\)"), "</span>");

    // Replace all \n with <br>
    markdown = std::regex::replace(markdown, std::regex("\n"), "<br>");

    // Replace all \* with <ul>
    markdown = std::regex::replace(markdown, std::regex("\\\\*"), "<ul>");

    // Replace all _ with <li>
    markdown = std::regex::replace(markdown, std::regex("\\\\_"), "<li>");

    // Replace all \n with <p>
    markdown = std::regex::replace(markdown, std::regex("\n"), "<p>");

    // Convert Markdown to HTML
    std::istringstream iss(markdown);
    std::vector<std::string> lines;
    copy(iss, back_inserter(lines));

    // Build HTML string
    html = "<html><body>";
    for (const std::string& line : lines) {
        html += "<p>" + line + "</p>";
    }
    html += "</body></html>";

    return html;
}
```
