        self.stack1 = []
        self.stack2 = []

    def empty(self):
        return (len(self.stack1) == 0 and len(self.stack2) == 0)

    def enqueue(self, item):
        if self.empty():
            self.stack1.append(item)
            self.stack2.append(None)
        else:
            self.stack2.append(item)

    def dequeue(self):
        if not self.empty():
            if self.stack1[-1] == None:
                item = self.stack2[-1]
                self.stack2.pop()
                self.stack1.append(item)
            else:
                self.stack1.pop()
                self.stack2.pop()

def find_common(arr1, arr2):
    res = []
    for i in arr1:
        if i in arr2:
            res.append(i)
    return res
