#include <iostream>
#include <fstream>
#include <string>
#include <map>
#include <sstream>

int main() {
    // Open the input file
    std::ifstream input_file("input.txt");
    if (!input_file.is_open()) {
        std::cerr << "Error: Could not open input file." << std::endl;
        return 1;
    }

    // Read the word to search for from the command line or standard input
    std::cout << "Enter the word to search for: ";
    std::string search_word;
    std::getline(std::cin, search_word);

    // Initialize a map to store the word count
    std::map<std::string, int> word_count;

    // Read each line of the input file and update the word count
    std::string line;
    while (std::getline(input_file, line)) {
        std::stringstream ss(line);
        std::string token;
        int count = 0;
        while (ss >> token) {
            if (token == search_word) {
                count++;
            }
        }
        // Update the word count map for each line
        for (const auto& kv : word_count) {
            if (line.find(kv.first) != std::string::npos) {
                kv.second += count; // update the word count for this line as well as any previous occurrences in the file
            }
        }
        // Add the word count for this line only (excluding previous occurrences)
        word_count[line] = count;
    }

    // Print the final word count map
    for (const auto& kv : word_count) {
        std::cout << kv.first << ": " << kv.second << std::endl;
    }

    return 0;
}
```

