        htmlText += HTMLTag::I + mdText.substr(pos, pos + 1) + mdText.substr(pos + 1, mdText.find("\n", pos)) + "</" + HTMLTag::I + ">\n";
        pos += mdText.find("\n", pos) + 1;
    }
    while ((pos = mdText.find("_", pos))!= std::string::npos) {
        htmlText += HTMLTag::U + mdText.substr(pos, pos + 1) + mdText.substr(pos + 1, mdText.find("\n", pos)) + "</" + HTMLTag::U + ">\n";
        pos += mdText.find("\n", pos) + 1;
    }
    while ((pos = mdText.find("[", pos))!= std::string::npos) {
        std::size_t linkPos = mdText.find("]", pos);
        std::size_t spacePos = mdText.find(" ", pos);
        std::size_t hrefPos = (linkPos > spacePos)? linkPos : spacePos;
        std::string linkText = mdText.substr(pos, hrefPos - pos);
        std::string linkUrl = mdText.substr(spacePos + 1, linkPos - spacePos - 1);
        htmlText += HTMLTag::A + " href=\"" + linkUrl + "\"";
        htmlText += mdText.substr(pos, linkPos) + "</" + HTMLTag::A + ">" + linkText + "</" + HTMLTag::A + ">\n";
        pos = hrefPos;
    }
    while ((pos = mdText.find("!", pos)) == 0 && mdText.size() > pos + 1 && mdText[pos + 1] == '[') {
        std::size_t imgPos = mdText.find("]", pos + 2);
        std::size_t spacePos = mdText.find(" ", pos + 2);
        std::size_t srcPos = (imgPos > spacePos)? imgPos : spacePos;
        std::string imgSrc = mdText.substr(spacePos + 1, imgPos - spacePos - 1);
        htmlText += HTMLTag::IMG + " src=\"" + imgSrc + "\"\n";
        mdText.erase(pos, srcPos + 1);
        pos = 0;
    }
    while ((pos = mdText.find("---", pos)) == 0) {
        htmlText += HTMLTag::HR + "\n";
        mdText.erase(pos, 3);
        pos = 0;
    }
    while ((pos = mdText.find("\n\n", pos))!= std::string::npos) {
        htmlText += HTMLTag::P + mdText.substr(pos, pos + 2) + "</" + HTMLTag::P + ">\n";
        pos += mdText.find("\n", pos + 2) + 1;
    }
    htmlText += mdText.substr(pos);
    return htmlText;
}
